Dictionary PremixedLaminarFlame1D
{
	@KineticsFolder  1;	

	@Type			BurnerStabilized;


	@InletStream 	inlet-stream;
	@OutletStream	outlet-stream;

	@InletVelocity  8.79 cm/s; 

	@Grid			grid;

	@Output			Output_1;
	@Backup		Output_0/Output.xml;
	@UseDaeSolver	true;

	@DaeParameters				dae-parameters;
	@NlsParameters				nls-parameters;
	@FalseTransientParameters	falsetransient-parameters;

	@FixedTemperatureProfile T-Profile;

	//@PolimiSoot					polimi-soot;
}

Dictionary inlet-stream
{
	@MoleFractions CH4 0.18 O2 0.20  N2 0.62 ; 

	@Temperature	300 K ; 
    @Pressure		1 atm;
}

Dictionary outlet-stream
{
	@MoleFractions	H2O  0.16  N2   0.84;
	@Temperature	1280 K;
	@Pressure		1 atm;
}

Dictionary grid
{
	@Length					16 mm;
	@InitialPoints			12;
	@Type					database;

	@MaxPoints				40;
	@MaxAdaptivePoints		5;
	@GradientCoefficient	0.05;
	@CurvatureCoefficient	0.5;
}

Dictionary dae-parameters
{
	@DaeSolver				OpenSMOKE++;
	@Jacobian				Band;

	@RelativeTolerance		1e-6;
	@AbsoluteTolerance		1e-10;
	@MaximumNumberOfSteps	100000;
	@MaximumStep			1e3;
	@MinimumStep			1e-9;
	@InitialStep			1e-9;
	@MaximumOrder			5;

	@MeanResidualThreshold		1e-5;
	@VerbosityLevel				1;
}

Dictionary nls-parameters
{
	@NlsSolver					OpenSMOKE++;
	@Jacobian					TridiagonalBlock;

	@FunctionTolerance			1e-7;
	@StepTolerance				3e-11;
	@RelativeError				1.5e-8;

	@MaximumNumberOfIterations	200;
	@MaximumSetupCalls			10;
	@MaximumSubSetupCalls		5;

	@Scaling					1;
	@VerbosityLevel				0;

	@Strategy					NewtonGlobalization;
}

Dictionary falsetransient-parameters
{
	@FalseTransientSolver		OpenSMOKE++;
	@Jacobian					TridiagonalBlock;

	@FunctionTolerance			1e-8;
	@StepTolerance				3e-11;
	@RelativeError				1.5e-8;

	@MaximumNumberOfIterations	200;
	@MaximumSetupCalls			10;
	@MaximumSubSetupCalls		5;

	@Scaling					1;
	@VerbosityLevel				0;

	@Strategy					NewtonGlobalization;

	@StepsReusingJacobian		20;
	@StepsBeforeIncreasing		25;
	@MinimumNumberSteps			80;
	@InitialStep				1e-6;
	@IncrementFactor			10.;
	@MaximumStep				0.01;
	@DecrementFactor			2.;
	@MinimumStep				1e-9;
}


Dictionary T-Profile
{	
	@XVariable length;
	@YVariable temperature;

	@XUnits mm;
	@YUnits K;

	@Profile 0.0	300 1.0	400 2.0	501 2.6	600 2.9	650 3.2	690 3.4	750 3.6	787 4.2	1005 4.9	1180 6.0	1273 6.9	1315 7.9	1365 8.7	1393 9.4	1405 10.0	1418 10.9	1415 12.0	1386 12.9	1365 13.9	1351 14.9	1330 16.0	1280;
}

Dictionary polimi-soot
{
    // Label indicating the soot particles (default: BIN)
    @SootLabel  BIN;

    // Fractal dimension of soot particles (default: 1.8)
    @FractalDimension   3;//1.8;

    // Index of minimum discrete section which is considered soot (default: 5)
    @SootMinimumSection     5;

    // Index of minimum discrete section from which to apply the fractal dimension (default: 12)
    @SootMinimumFractalDimension    12;

    // Linear density: BINstart DENSITYstart BINend DENSITYend (default: 10 1500. 20 1700.)
    @Density    10 1800. 25 1800.;

    // Write the soot particle size distribution function (PSDF) on file (default: true)
    //@WritePSDF  false;//true;

    // Threshold (soot volume fraction) for writing the PSDF on file (default: 1e-11)
    //@ThresholdForPSDF   1e-11;

    // Radiative heat transfer from soot (default true)
    @RadiativeHeatTransfer    true;

    // Planck mean absorption coefficient for soot: Smooke (default) | Kent | Sazhin
    @PlanckCoefficient      Smooke;

    // Physical diffusion for soot particles (default: true)
    @PhysicalDiffusion      true;

    // Thermophoretic effect for soot sections (default: true)
    @ThermophoreticEffect   true;
}
